import Agent from '@discordjs/rest/node_modules/undici/types/agent'
import buildConnector from '@discordjs/rest/node_modules/undici/types/connector';
import Dispatcher from '@discordjs/rest/node_modules/undici/types/dispatcher'
import { IncomingHttpHeaders } from '@discordjs/rest/node_modules/undici/types/header'

export default ProxyAgent

declare class ProxyAgent extends Dispatcher {
  constructor(options: ProxyAgent.Options | string)

  dispatch(options: Agent.DispatchOptions, handler: Dispatcher.DispatchHandlers): boolean;
  close(): Promise<void>;
}

declare namespace ProxyAgent {
  export interface Options extends Agent.Options {
    uri: string;
    /**
     * @deprecated use opts.token
     */
    auth?: string;
    token?: string;
    headers?: IncomingHttpHeaders;
    requestTls?: buildConnector.BuildOptions;
    proxyTls?: buildConnector.BuildOptions;
    clientFactory?(origin: URL, opts: object): Dispatcher;
  }
}
